import React, {useState} from 'react';

import { useDispatch } from 'react-redux';
import './CreateArticle.scss';
export default function CreateArticle() {
    const [article,setArticle] = useState({
        title:"",
        body:""
    })
    const dispatch=useDispatch();
    const handleForm=e=>{
        e.preventDefault();
        console.log(article);
        dispatch({
            type:'ADDARTICLE',
            payload:article
        })
        setArticle({
            title:"",
            body:""
        })
    }

    const handleInputs=e=>{
        if(e.target.classList.contains('inp-title')){
            const newObjState={...article,title:e.target.value};
            setArticle(newObjState);
        }else if(e.target.classList.contains('inp-body')){
            const newObjState={...article,body:e.target.value};
            setArticle(newObjState);
        }
    }
    return(
        <div className="containerCreate">
           <h1>Ecrivez un article</h1>
           <form onSubmit={handleForm} className="container-form">
                <label>Titre</label>
                <input className="inp-title" value={article.tile} onChange={handleInputs} type="text" placeholder="Entrez votre nom"/>
                <label>Votre article</label>
                <textarea className="inp-body" value={article.body} onChange={handleInputs} name="" cols="30" rows="10" placeholder="Votre article"></textarea>
                <input type="submit" value="Envoyer"/>
           </form>
        </div>
    );
}
